jQuery(document).ready(function ($) {
	if ($('[data-toggle="tooltip"]').length) {
		$('[data-toggle="tooltip"]').tooltip({
			container: "body"
		})
	}
	if ($('[data-toggle="popover"]').length) {
		$('[data-toggle="popover"]').popover({
			container: "body",
			trigger: "hover",
            content: function () {
                return '<img src="'+$(this).data('img') + '" />';
            }
        })
	}
});
